const descriptionBox=document.getElementById("description-box");descriptionBox.classList.add("description");const descriptionTitle=document.createElement("div");descriptionTitle.classList.add("headline");const descriptionCutline=document.createElement("div");var title,cutline;descriptionCutline.classList.add("cutline"),descriptionBox.appendChild(descriptionTitle),descriptionBox.appendChild(descriptionCutline);for(var links=document.getElementById("proto-list").getElementsByTagName("a"),i=0;i<links.length;i++){function activateItem(e){let t=e.target.dataset.title;t&&(descriptionTitle.innerHTML=`<h4 class="headline">${t}</h4>`);let i=e.target.dataset.cutline;i&&(descriptionCutline.innerHTML=`<p class="caption">${i}</p>`)}links[i].addEventListener("click",activateItem),window.addEventListener("load",(e=>{descriptionTitle.innerHTML=`<h4 class="headline">${links[0].dataset.title}</h4>`,descriptionCutline.innerHTML=`<p class="caption">${links[0].dataset.cutline}</p>`}))}